<script>
import { gsap } from "gsap";

export default {
  name: "LogoAnimation",
  props: {
    width: {
      type: [String, Number],
      default: "100",
    },
    height: {
      type: [String, Number],
      default: "100",
    },
  },
  mounted() {
    // Анимация GSAP
    this.animateLogo();
  },
  methods: {
    animateLogo() {
      const logo = this.$refs.logo; // Ссылка на SVG
      gsap.to(logo, {
        scale: 1.1,
        duration: 1,
        yoyo: true,
        repeat: -1, // Бесконечная анимация
        transformOrigin: "center center", // Центр SVG
      });
    },
  },
};
</script>

<style scoped>
.logo {
  display: block;
  margin: auto;
}
</style>
